    public Map<String, Object> addToCart(@RequestParam("productId") int productId,
            @RequestParam("quantity") int quantity, HttpServletRequest request, HttpServletResponse response) {
        System.out.println("productId: " + productId + " quantity: " + quantity);
        Map<String, Object> responseData = new HashMap<>();
        try {
            Product product = productService.getProductById(productId);
            if (product.getStockQuantity() < quantity) {
                responseData.put("status", "error");
                responseData.put("message", "Not enough stock");
                return responseData;
            }
            cartService.addToCart(CartDTOMapper.entityToDto(product, quantity), request, response);
        } catch (RuntimeException e) {
            responseData.put("status", "error");
            responseData.put("message", "Add to cart failed");
